import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Container, Row, Col, Form, Button, Alert,Card } from "react-bootstrap";
import { useState, Fragment } from "react";
import ProfileImageUpload from "./ProfileImageUpload";
import Header from "../Header";
import Footer from "../Footer";
// import "./ProfileImageUpload.css";

class UserProfilePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      validated: false
    }
  }

  onChange = () => {
    const password = document.querySelector("input[name=password]");
    const confirm = document.querySelector("input[name=confirmPassword]");
    if (confirm.value === password.value) {
      confirm.setCustomValidity("");
    } else {
      confirm.setCustomValidity("Passwords do not match");
    }
  };

  handleSubmit = (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }

    this.setState({ validated: true });
  };
  // const [validated, setValidated] = useState(false);

  // const onChange = () => {
  //   const password = document.querySelector("input[name=password]");
  //   const confirm = document.querySelector("input[name=confirmPassword]");
  //   if (confirm.value === password.value) {
  //     confirm.setCustomValidity("");
  //   } else {
  //     confirm.setCustomValidity("Passwords do not match");
  //   }
  // };

  // const handleSubmit = (event) => {
  //   const form = event.currentTarget;
  //   if (form.checkValidity() === false) {
  //     event.preventDefault();
  //     event.stopPropagation();
  //   }

  //   setValidated(true);
  render() {
  return (
    <div>
      <div>
        <Header />
        <div className="container2">
          <Container className="mt-5 justify-content-md-center ">
            <Row>
              <Col md={2}></Col>
              <Col>
                <Card
                  style={{ width: "50rem" }}
                  className="mt-5 justify-content-md-center"
                >
                  <Row className="mt-5 justify-content-md-center">
                    <Col md={8}>
                      <Row>
                        <Col md={6}>
                          <h1 style={{ color: "#1EABE3" }}>
                            Change Your Profile
                          </h1>
                        </Col>
                        <Col md={2}>
                          <ProfileImageUpload />
                        </Col>
                      </Row>

                      <Form
                        noValidate
                        validated="validated"
                        onSubmit="handleSubmit"
                      >
                        <Form.Group
                          className="mb-3"
                          controlId="validationCustom01"
                        >
                          <Form.Label>Your name</Form.Label>
                          <Form.Control
                            required
                            type="text"
                            defaultValue="John"
                            name="name"
                          />
                          <Form.Control.Feedback type="invalid">
                            Please enter a name
                          </Form.Control.Feedback>
                        </Form.Group>
                        <Form.Group
                          className="mb-3"
                          controlId="formBasicLastName"
                        >
                          <Form.Label>Your last name</Form.Label>
                          <Form.Control
                            required
                            type="text"
                            defaultValue="Doe"
                            name="lastName"
                          />
                          <Form.Control.Feedback type="invalid">
                            Please enter your last name
                          </Form.Control.Feedback>
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="formBasicEmail">
                          <Form.Label>Email address</Form.Label>
                          <Form.Control
                            disabled
                            value="john@doe.com   if you want to change email, remove account and create new one with new email address"
                          />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="formBasicPhone">
                          <Form.Label>Phone number</Form.Label>
                          <Form.Control
                            type="text"
                            placeholder="Enter your phone number"
                            defaultValue=""
                          />
                        </Form.Group>
                        <Form.Group
                          className="mb-3"
                          controlId="formBasicAddress"
                        >
                          <Form.Label>Address</Form.Label>
                          <Form.Control
                            type="text"
                            placeholder="Enter your street name and house number"
                            defaultValue=""
                          />
                        </Form.Group>
                        <Form.Group
                          className="mb-3"
                          controlId="formBasicCountry"
                        >
                          <Form.Label>Country</Form.Label>
                          <Form.Control
                            type="text"
                            placeholder="Enter your country"
                            defaultValue=""
                          />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="formBasicZip">
                          <Form.Label>Zip Code</Form.Label>
                          <Form.Control
                            type="text"
                            placeholder="Enter your Zip code"
                            defaultValue=""
                          />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="formBasicCity">
                          <Form.Label>City</Form.Label>
                          <Form.Control
                            type="text"
                            placeholder="Enter your city"
                            defaultValue=""
                          />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="formBasicState">
                          <Form.Label>State</Form.Label>
                          <Form.Control
                            type="text"
                            placeholder="Enter your state"
                            defaultValue=""
                          />
                        </Form.Group>
                        <Form.Group
                          className="mb-3"
                          controlId="formBasicPassword"
                        >
                          <Form.Label>Password</Form.Label>
                          <Form.Control
                            name="password"
                            required
                            type="password"
                            placeholder="Password"
                            minLength={6}
                            onChange="onChange"
                          />
                          <Form.Control.Feedback type="invalid">
                            Please anter a valid password
                          </Form.Control.Feedback>
                          <Form.Text className="text-muted">
                            Password should have at least 6 characters
                          </Form.Text>
                        </Form.Group>
                        <Form.Group
                          className="mb-3"
                          controlId="formBasicPasswordRepeat"
                        >
                          <Form.Label>Repeat Password</Form.Label>
                          <Form.Control
                            name="confirmPassword"
                            required
                            type="password"
                            placeholder="Repeat Password"
                            minLength={6}
                            onChange={onChange}
                          />
                          <Form.Control.Feedback type="invalid">
                            Both passwords should match
                          </Form.Control.Feedback>
                        </Form.Group>

                        <Button variant="primary" type="submit">
                          Update
                        </Button>
                        <Alert show={true} variant="danger">
                          User with that email already exists!
                        </Alert>
                        <Alert show={true} variant="info">
                          User updated
                        </Alert>
                      </Form>
                    </Col>
                  </Row>
                </Card>
              </Col>
              <Col md={2}>
                <Card
                  style={{ width: "18rem" }}
                  variant="flush"
                  className="mt-5"
                >
                  <Card.Img
                    crossorigin="anonymous"
                    variant="top"
                    src="holder.js/100px180"
                  />
